
/**
 * Upload example.
 */

var fs = require('fs');
var CloudXLS = require('../');
var cloudxls = new CloudXLS('YOUR-API-KEY');

var csv = fs.createReadStream(__dirname + '/hello-world.csv');

cloudxls.async({
  data: {
    file: csv
  },
  sheet: {
    name: 'Hello World'
  },
  doc: {
    filename: 'hello-world'
  }
}, function(err, res, body){
  console.log(body);
  // { uuid: '409a9ff7-f02a-4727-98b8-2321e9992af6',
  //   url: 'https://cloudxls.com/b/download/409a9ff7-f02a-4727-98b8-2321e9992af6/hello-world.xls' }
});
